    private void butUpdateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_butUpdateActionPerformed
        // TODO add your handling code here:
        model.setValueAt(txtMaSV.getText(), i, 0);
        model.setValueAt(txtHoTen.getText(), i, 1);
        model.setValueAt(txtNgaySinh.getText(), i, 2);
        model.setValueAt((radioNam.isSelected() ? "Nam" : "Nu"), i, 3);
        model.setValueAt(comboNganhHoc.getSelectedItem().toString(), i, 4);
        model.setValueAt(txtDiemTB.getText(), i, 5);
        model.setValueAt(xepLoai(), i, 6);
    }//GEN-LAST:event_butUpdateActionPerformed

