    private void jTable1MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jTable1MouseClicked
        // TODO add your handling code here:
        i = jTable1.getSelectedRow();
        if (evt.getClickCount() == 2) {
            txtMaSV.setText(model.getValueAt(i, 0).toString());
            txtHoTen.setText(model.getValueAt(i, 1).toString());
            txtNgaySinh.setText(model.getValueAt(i, 2).toString());
            if (model.getValueAt(i, 3).toString().equalsIgnoreCase("Nam")) {
                radioNam.setSelected(true);
            } else {
                radioNu.setSelected(true);
            }
            comboNganhHoc.setSelectedItem(model.getValueAt(i, 4));
            txtDiemTB.setText(model.getValueAt(i, 5).toString());
        }
    }//GEN-LAST:event_jTable1MouseClicked

    private void btLamMoiActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btLamMoiActionPerformed
        txtMaSV.setText("");
        txtHoTen.setText("");
        txtNgaySinh.setText("");

        bg.clearSelection();
        txtDiemTB.setText("");
        comboNganhHoc.setSelectedIndex(0);
        txtMaSV.requestFocus();
        
    }//GEN-LAST:event_btLamMoiActionPerformed

    private void butDelete1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_butDelete1ActionPerformed
        // TODO add your handling code here:
        int n = jTable1.getRowCount();
        if (n == 0) {
            JOptionPane.showMessageDialog(null, "khong co du lieu de xoa");
        } else {//n!=0
            int i = jTable1.getSelectedRow();
            if (i!=-1) {
                 model.removeRow(i);
            } else {
                JOptionPane.showMessageDialog(null, "Chua chon dong de xoa");
            }
        }
        txtMaSV.setText("");
        txtHoTen.setText("");
        txtNgaySinh.setText("");

        bg.clearSelection();
        txtDiemTB.setText("");
        comboNganhHoc.setSelectedIndex(0);
        txtMaSV.requestFocus();
    }//GEN-LAST:event_butDelete1ActionPerformed

