    private void myInit() {
        model = (DefaultTableModel) jTable1.getModel();
        jTable1.setModel(model);
//	jTable1.setFont(new Font(" ",0, 20));
        jTable1.setRowHeight(22);

//	jTable1.getTableHeader().setFont(new Font("...",0,20));
        bg = new ButtonGroup();
        bg.add(radioNam);
        bg.add(radioNu);
        setLocationRelativeTo(null);
    }

    public void themMotDong() {
        model.addRow(new Object[]{txtMaSV.getText(), txtHoTen.getText(), txtNgaySinh.getText(),
            (radioNam.isSelected() ? "Nam" : "Nu"), comboNganhHoc.getSelectedItem().toString(), txtDiemTB.getText(), xepLoai()});
    }

    private String xepLoai() {
        float diemTB = Float.parseFloat(txtDiemTB.getText());
        if (diemTB >= 8) {
            return "Gioi";
        } else if (diemTB >= 6.5) {
            return "Kha";
        } else if (diemTB >= 5) {
            return "TB";
        } else {
            return "Yeu";
        }
    }

    public boolean timTheoMaSV(String maSV) {
        for (int i = 0; i < jTable1.getRowCount(); i++) {
            if (model.getValueAt(i, 0).toString().equalsIgnoreCase(maSV));
            return true;
        }
        return false;
    }

